\begin{figure}
  \includegraphics[width=0.45\textwidth]{loop_generation_stable_loop_velocities}
  \caption{The velocity distribution of stable loops for various
    resolutions and numbers of modes.  The distribution is universal and
    sharply peaked at $v\sim 1$. Also shown are the SP (solid, gray line)
    and CA (dashed, gray line) for comparison.  The resolution of SP did
    not allow them to produce high velocity loops. Our results refine
    the peak at high velocity found by CA\@.}
  \label{fig:velocity-distribution}
\end{figure}

\begin{eqnarray}
  A_{ij} & = & \int P_i P_j\, d\sigma_- , \nonumber \\
  B_{ij} & = & \int Q_i Q_j\, d\sigma_+.
  \label{eqn:moment-of-inertia}
\end{eqnarray}

\begin{figure}
  \includegraphics[width=0.45\textwidth]{eigenvalue_generation_averages}
  \caption{The average of the eigenvalues of the $\vec P$ and $\vec Q$
    ``moment of inertia'' tensors~(\ref{eqn:moment-of-inertia}) versus
    loop generation.  Since both $\vec P$ and $\vec Q$ have the same
    properties, both are included in the average to increase the
    statistics. We notice that the smaller eigenvalue approaches zero and
    the largest eigenvalue is much larger than the middle eigenvalue
    showing that the $\vec P$ and $\vec Q$ curves are localized on the
    KT-sphere. This plot is for $N=10,000$, $M=10$.  The asymptotic
    behavior is independent of resolution and the number of modes.}
    \label{fig:eigenvalue-average}
\end{figure}

\begin{figure}
  \includegraphics[width=0.45\textwidth]{kink_sharpness_histogram.pdf}
  \caption{The kink sharpness distribution (see
    Eq.~\ref{eq:kinksharpness}).  The distribution is independent of
    resolution and number of modes on the initial string.  We see that the
    distribution is sharply peaked at $1$, that is where the $\vec P$
    before and after the kink are anti-parallel.}
  \label{fig:kinksharpnesshistograms}
\end{figure}

