\begin{figure}
  \includegraphics[height=2.5in]{pq_KT_sphere.pdf}
  \caption{The ${\vec P}$ and $-{\vec Q}$ curves plotted on the KT-sphere
    for a stable loop from our simulations.  This loop contains three kinks
    and one cusp.  The two curves are represented by the dots.  The $\vec
    Q$ curve is the dots along the equator.  The $\vec P$ curve is the dots
    perpendicular to the equator (running vertically on the left of the
    sphere) and includes the lone anti-podal point (on the right of the
    sphere). Note that the points on the sphere are not weighted
    equally and the centroids of both $\vec P$ and $\vec Q$ curves
    are at the center of the sphere.}
  \label{fig:stablecuspyloop}
\end{figure}

\begin{figure}
  \includegraphics[width=0.45\textwidth]{length_binned_loop_velocity_range}
  \caption{The velocity of stable loops versus the length of the loop. The
    $x$ error bars show the width of the length bins employed and the $y$
    error bars show the $95$ percentile ranges in each length bin. The
    square symbol shows the average in the bin.  Short loops have center of
    mass velocity $\sim 1/\sqrt{2}$ which is the root-mean-squared velocity
    of the string in the initial loop. Longer loops tend to have a lower
    velocity consistent with the fact that the initial loop is at rest.}
  \label{fig:velocityvslength}
\end{figure}

\begin{figure}
  \includegraphics[width=0.45\textwidth]{length_binned_loop_kink_counts_range}
  \caption{Similar to Fig.~\ref{fig:velocityvslength} now for the number of
    kinks on stable loops versus the length of the loop. Longer loops tend
    to have a larger number of kinks though the variation is slight. The
    loop with the maximum number of kinks has 8 kinks instead of the
    canonical 4.}
  \label{fig:kinksvslength}
\end{figure}

\begin{figure}
  \includegraphics[width=0.45\textwidth]{length_binned_cusp_counts_range}
  \caption{Similar to Fig.~\ref{fig:velocityvslength} now for the average
    number of cusps on stable loops versus length of the loop. Longer loops
    typically contain more cusps on average. The loop with the maximum
    number of cusps has only 3 cusps.}
  \label{fig:cuspsvslength}
\end{figure}

\begin{figure}
  \includegraphics[width=0.45\textwidth]{length_binned_loop_eigenvalue_range}
  \caption{Similar to Fig.~\ref{fig:velocityvslength} now for the
    eigenvalues of the moment of inertia tensors of the stable loops versus
    length. Longer loops are also planar and only slightly less so than
    smaller loops.}
  \label{fig:eigenvaluesvslength}
\end{figure}

